vec4 rect(vec2 uv, vec2 pos, vec2 dim, vec3 col, vec3 bcol){

	if ((uv.x>=pos.x && uv.x<=pos.x+dim.x) && (uv.y>=pos.y && uv.y<=pos.y+dim.y)) {
		return vec4(col,1.0);
	}
	return vec4(bcol,1.0);
}

