float sinNormalized(float x)
{
    return 0.5 + 0.5*sin(3.1415*x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 tint = vec4(1.0, 1.0, 1.0, 1.0);

    float skySpeed = 0.1;

    if (uv.y > (0.3 + 0.1*sinNormalized(uv.x)))
    {
        // sky
        uv.x += sinNormalized(skySpeed * iTime);
        tint = vec4(0.3, 0.3, 3.0, 1.0);
    }
    else
    {
        // grass
        tint = vec4(0.3, 1.0, 0.3, 1.0);
    }
	fragColor = tint * texture(iChannel0, uv);
}
