void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy / iResolution.xy;
    xy.y = 1.0 - xy.y; // flip image y axis to match canvas
    vec4 texColour = texture(iChannel0, xy); // get pixel from input img

    // all the magic numbers
    float luminance = texColour.r * 0.299 + texColour.g * 0.587 + texColour.b * 0.114;

    fragColor = vec4(luminance, luminance, luminance, 1.0);
}
