float checker(vec2 uv){
    vec2 uv2 = uv;
    float tiling = 3.0;
    float hardness = 64.0;

    uv = mod(uv*tiling,1.0);
    uv2 = mod(uv2*(tiling/2.0),1.0);

    vec2 f;
    f.x = abs(uv.x-uv.y);
    f.y = abs(uv.y- (1.0- uv.x));

    float c = (f.x+f.y);
    c = pow(c,hardness);
    c = mix(1.0,c,  clamp((uv2.x-0.5)/(uv2.y-0.5)*10000.0, 0.0, 1.0));
    return clamp(c, 0.0, 1.0);

}

float diamond(vec2 uv){
    float tiling = 1.5;
    uv = mod(uv*tiling,1.0);
    float hardness = 32.0;

    vec2 f;
    vec2 p = vec2(0.5,0.5);

    f.x = length(uv.x - p);
    f.y = length(uv.y - p);

    float c = (f.x+f.y)+0.29;
    c = pow(c,hardness);
    return clamp(c, 0.0, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv*= 2.0;



    vec3 prim = vec3(0.8,0.5,0.1);
    vec3 sec = vec3(0.9,0.6,0.3);

    vec3 last = vec3(0.5,0.1,0.1);
    vec3 fin = vec3(0.3,0.1,0.1);



    vec3 color = mix(prim,sec, checker(uv));
    color = mix(color,sec, diamond(uv)* (1.0 -checker(uv)));
    color = mix(color,prim, diamond(uv)* (checker(uv)));

	fragColor = vec4(color,1.0);

}
