void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 olduv = uv;
    float asp = iResolution.y / iResolution.x;

    uv.x = floor(uv.x * 100.0) / 100.0;
    uv.y = floor(uv.y * 100.0 * asp) / (100.0 * asp);

    float dc = sqrt(pow(uv.x - 0.5, 2.0) + pow(uv.y - 0.5, 2.0));

    float angle = 2.0 * 3.14 * iTime * uv.x * uv.y;
    float mag = 0.01 * (dc);
    float ox = mag * cos(angle);
    float oy = mag * sin(angle);
    uv.x += ox;
    uv.y += oy;
    float r = texture(iChannel0, uv).r;
    r = r * r;
    r = floor(r * 5.0);
    r = min(r, 1.0);

    if (int(floor(olduv.x * 100.0)) % 2 == int(floor(olduv.y * 100.0 * asp)) % 2 && r == 0.0) {
    	r = 0.2;   
    }
    fragColor.g = r / 4.0;   

	fragColor.r = r;

}
