float drawCircle(in float radius, in vec2 position, in vec2 uv){   
   float alpha = step(radius, length(position-uv));
    return alpha;
}

float drawRect(in vec2 position, in vec2 sideLength, in vec2 uv){
	float alpha;
    alpha = step(position.x, uv.x);
    alpha = step(position.y, uv.y)*alpha;
    alpha = step(uv.y - sideLength.y, position.y)*alpha;
    alpha = step(uv.x-sideLength.x, position.x)*alpha;
    return alpha;

}


vec4 red = vec4(1.0, 0.0, 0.0, 1.0);
vec4 green = vec4(0.0, 1.0, 0.0, 1.0);
vec4 blue = vec4(0.0, 0.0, 1.0, 1.0);

vec2 position = vec2(0.1, 0.0);
vec2 side = vec2(0.8, 0.7);

vec2 chimney = vec2(0.8, 0.7);
vec2 chimneySide = vec2(0.1, 0.25);

vec2 win1 = vec2(0.6, 0.4);
vec2 win1Side = vec2(0.2, 0.15);

vec2 win2 = vec2(0.2, 0.4);
vec2 win2Side = vec2(0.2, 0.15);

vec2 win3 = vec2(0.6, 0.1);
vec2 win3Side = vec2(0.2, 0.15);

vec2 win4 = vec2(0.2, 0.1);
vec2 win4Side = vec2(0.2, 0.15);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //set the variables to 0.0 to make a layer invisible.
    float houseOpacity = 1.0;
    float chimneyOpacity = 1.0;
    float windowsOpacity = 1.0;

    //layer1 = house, layer2 = chimney, layer3 = windows.
    vec4 layer1, layer2, layer3;
    vec2 uv = fragCoord.xy / iResolution.xy;


    layer1 = blue;
    layer1.a = drawRect(position, side, uv);
    layer1.a*=houseOpacity;

    layer2 = red;
    layer2.a = drawRect(chimney, chimneySide, uv);
    layer2.a*=chimneyOpacity;

    layer3 = green;
    layer3.a = drawRect(win1, win1Side, uv);
    layer3.a = drawRect(win2, win2Side, uv) + layer3.a;
    layer3.a = drawRect(win3, win3Side, uv) + layer3.a;
    layer3.a = drawRect(win4, win4Side, uv) + layer3.a;
    layer3.a*=windowsOpacity;



	vec4 tempColor = vec4(uv,0.5+0.5*sin(iTime),1.0);

    //comment out a line to disable a layer.
    tempColor = mix(tempColor, layer1, layer1.a);
    tempColor = mix(tempColor, layer2, layer2.a);
    tempColor = mix(tempColor, layer3, layer3.a);

	fragColor = tempColor;
}
