#define SPEED 5.0
#define OFFSET .009  

vec2 offset(vec2 uv, float amtX, float amtY)
{
    return vec2(uv.x+amtX, uv.y+amtY);
}


// https://www.shadertoy.com/view/Mds3zn
// https://gamedev.stackexchange.com/questions/58408/how-would-you-implement-chromatic-aberration
vec3 chromatic(vec2 uv)
{
    // offset channels
    float o_x = (1. + sin(iTime*SPEED)) * OFFSET;

    return vec3(texture(iChannel0, offset(uv, o_x,  0.)).r,
                texture(iChannel0, offset(uv, 0.,   0.)).g,
                texture(iChannel0, offset(uv,-o_x,  0.)).b);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 c = chromatic(uv);

	fragColor = vec4(c,1.0);
}
