const vec4 color0 = vec4(1.0, 0.0, 0.2, 1.0);
const vec4 color1 = vec4(0.6, 1.0, 0.1, 1.0);

vec4 fromLinear(vec4 linearRGB)
{
	bvec4 cutoff = lessThan(linearRGB, vec4(0.0031308));
	vec4 higher = vec4(1.055)*pow(linearRGB, vec4(1.0/2.4)) - vec4(0.055);
	vec4 lower = linearRGB * vec4(12.92);

	return mix(higher, lower, cutoff);
}

vec4 toLinear(vec4 sRGB)
{
	bvec4 cutoff = lessThan(sRGB, vec4(0.04045));
	vec4 higher = pow((sRGB + vec4(0.055))/vec4(1.055), vec4(2.4));
	vec4 lower = sRGB/vec4(12.92);

	return mix(higher, lower, cutoff);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 from;
	vec2 to;
	vec2 gradVec;
	float value;

	if (iMouse == vec4(0.0)) {
		from = 0.2*iResolution.xy;
		to = iResolution.xy;
	} else {
		from = abs(iMouse.zw);
		to = iMouse.xy;
	}

	gradVec = to - from;
	value = dot(fragCoord.xy - from, gradVec)/dot(gradVec, gradVec);
	value = clamp(value, 0.0, 1.0);

	fragColor = fromLinear(mix(toLinear(color0), toLinear(color1), value));
}
