vec3 orange = vec3(1.0,0.6,0.2);
vec3 red = vec3(1.0,0.0,0.0);
vec3 lightB = vec3(0.0,1.0,1.0);

vec4 BG = vec4(0.0,0.0,0.0,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 Layer0 = BG;
    vec4 Layer1, Layer2,Layer3, Layer4;

	// creating the varibles for the rectangles
    float L1Step, L2Step, L3Step, L4Step;

    //creating the orange body of the building
    float L1StepX = step(0.3, uv.x) * step(uv.x,0.7); // multiplying two steps together gives A&B
    float L1StepY = step(0.1,uv.y) * step(uv.y, 0.5); // multiplying two steps together give C&D
    L1Step = L1StepX * L1StepY; // multiplying these two gives a (A&B) & (C&D)

    float L2StepX = step(0.6, uv.x) * step(uv.x,0.7);
    float L2StepY = step(0.5,uv.y) * step(uv.y, 0.6);
    L2Step = L2StepX * L2StepY;

	float L3StepX = step(0.55, uv.x) * step(uv.x,0.65);
    float L3StepY = step(0.25,uv.y) * step(uv.y, 0.4);
    L3Step = L3StepX * L3StepY;    

    float L4StepX = step(0.35, uv.x) * step(uv.x,0.45);
    float L4StepY = step(0.25,uv.y) * step(uv.y, 0.4);
    L4Step = L4StepX * L4StepY;

    // creating the layers to a color that is fixed by their alphas
    Layer1 = vec4(orange, L1Step);
    Layer2 = vec4(red, L2Step);
    Layer3 = vec4(lightB, L3Step);
    Layer4 = vec4(lightB, L4Step);

    // mixing the background into the layers
   	vec4 Layer0_1Mix = mix(Layer0,Layer1,Layer1.a);
    vec4 Layer0_2Mix = mix(Layer0, Layer2, Layer2.a);
    vec4 Layer0_3Mix = mix(Layer0, Layer3, Layer3.a);
    vec4 Layer0_4Mix = mix(Layer0, Layer4, Layer4.a);

    // mixing the layers together
    vec4 L1_2Mix = mix(Layer0_1Mix, Layer0_2Mix, Layer2.a);
    vec4 L3_4Mix = mix(Layer0_3Mix, Layer0_4Mix, Layer4.a);


    //mixing the mix of the layers together
    fragColor = L1_2Mix + L3_4Mix;


}
