void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 red = vec4( 1.0, 0.0, 0.0, 1.0 );
    vec4 green = vec4( 0.0, 1.0, 0.0, 1.0 );

	vec2 uv = fragCoord.xy / iResolution.xy;

    if( mod( uv.y + iTime * 0.1, 0.2 ) > 0.01 + ( 0.5 * 0.5 + sin( uv.x * 100.0 + iTime * 10.0) ) * 0.01 &&
        mod( uv.y + iTime * 0.1, 0.2 ) < 0.1 + ( 0.5 * 0.5 + sin( uv.x * 100.0 + -iTime * 10.0) ) * 0.01)
        fragColor = red;
    else
        fragColor = green;

}
