#define BLUR 16.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = .3*texture(iChannel0,uv)+(3.+3.*sin(iTime))*texture(iChannel1,uv/BLUR);
}

//buf a
#define BLUR 16.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(fragCoord.x < iResolution.x/BLUR && fragCoord.y < iResolution.y/BLUR)
    {
    	vec2 uv = fragCoord/iResolution.xy*BLUR;
        vec4 t = texture(iChannel0,uv);
        t = pow(t,vec4(4.))*.3;
        t = vec4(length(t.xyz));
		fragColor = t;
    }
    else
        discard;
}
