#define PI 3.1415
#define raysCountMin 0.0
#define raysCountMax 80555.0
#define pixelCountMin 1440.0
#define pixelCountMax 6000.0
#define softness 1.0
#define speed 10.0

float GetMoireValueAt(vec2 coord, float timeShift)
{

    float rv = 0.0;
    //float animatedRayCount = sin(iTime*speed + PI * timeShift) * 0.5 + 0.5;
    //animatedRayCount = mix(raysCountMin,raysCountMax,animatedRayCount);
    //animatedRayCount = sqrt(iTime/80000.0)*80000.0;
    //animatedRayCount = (floor(iTime*30.0)/30.0)*150.0;
    float timeAccelerated = iTime*iTime;
    float animatedRayCount = 85000.0 + (floor((speed*timeAccelerated+timeShift)*30.0)/30.0);
    // -> cool animatedRayCount = 80000.0 + (floor(iTime*30.0)/30.0)*5.0;
    //animatedRayCount *= 1.0+iTime;


    rv = (((atan(coord.x,coord.y) + PI) / (PI)));
    rv *= animatedRayCount;
    rv = abs((mod(rv, 2.0) - 1.0));
    rv = smoothstep(0.0,softness,rv);

    return rv;
}

float SA(float x)
{
    return sin(x) * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 sampleUV = uv;

    uv -= vec2(0.5,0.5);

    //uv.x = abs(uv.x);
    uv.x = 0.5+(uv.x);

   // uv.x = abs(uv.x);
    vec2 uv2 = uv;
    uv2.x = 1.0-(uv.x);

    float pixCount = mix(pixelCountMin,pixelCountMax,SA(iTime));
    //uv = floor(uv*pixCount)/pixCount;

    vec4 col = vec4(0.0,0.0,0.0,1.0);
    col.r = GetMoireValueAt(uv,0.0);
    col.g = GetMoireValueAt(uv,0.005);
    col.b = GetMoireValueAt(uv,-0.002);

    vec4 col2 = vec4(0.0,0.0,0.0,1.0);
    col2.r = GetMoireValueAt(uv2,0.0);
    col2.g = GetMoireValueAt(uv2,0.005);
    col2.b = GetMoireValueAt(uv2,-0.002);



    col *= sqrt(1.0-sampleUV.x);
    col2 *= sqrt(sampleUV.x);

    col = max(col,col2);
    //col = col*col2;

	vec4 oldCol = texture( iChannel0, vec2(sampleUV.x,sampleUV.y) );
    float beatMix = pow((sin(iTime * 5.0) * 0.5 + 0.5),8.0);
    vec4 mixedCol = (col-0.5)*12.0; //contrast
    vec4 diff = mixedCol-oldCol;
    mixedCol = mix(oldCol,mixedCol,0.05); //lowpass


	fragColor = mixedCol;
}
