float superLength(vec2 v,float n){
    v = abs(v);
	return pow( pow(v.x,n)+pow(v.y,n),1.0/n);
}

vec2 getTunnelCoord(vec2 screenPos) {
 	float angle = atan(screenPos.y,screenPos.x);
    float z = 0.1/(superLength(screenPos,0.5+5.0*(0.5+0.5*sin(iTime))));

    return vec2(angle/3.1415,z*1.0);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 screenPos = 2.0*(vec2(-0.5,-0.5)+fragCoord.xy / iResolution.xy);
    screenPos.x*=iResolution.x/iResolution.y;

    vec2 xy = getTunnelCoord(screenPos); 

    vec2 fixedCoord = vec2(screenPos.x + sin(xy.y*3.0+iTime)*0.2,screenPos.y);

    // *** scroll
    vec2 uv = xy+vec2(xy.y+5.0*sin(iTime*0.1+sin(iTime *0.3)),iTime*0.1);


    vec4 color = texture(iChannel0,uv)+texture(iChannel1,screenPos.xy);

    fragColor = color;
}
