void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = (iTime*0.05);

    vec2 p = fragCoord.xy / iResolution.xy;

    vec3 T = texture( iChannel1, p ).xyz;
    float Z = clamp( texture( iChannel0, p*1.0+time ).x, 0.7, 1.0 );

 	vec3 D = smoothstep( 0.8, 1.0, T );

    for( float i=1.0;i<17.0;i+=2.0 ){
        float s = i/200.0;
        D = D + ( smoothstep( 0.8, 1.0, texture( iChannel1, vec2( p.x+s, p.y ) ).xyz ) /i);
        D = D + ( smoothstep( 0.8, 1.0, texture( iChannel1, vec2( p.x-s, p.y ) ).xyz ) /i);
        D = D + ( smoothstep( 0.8, 1.0, texture( iChannel1, vec2( p.x, p.y+s ) ).xyz ) /i);
        D = D + ( smoothstep( 0.8, 1.0, texture( iChannel1, vec2( p.x, p.y-s ) ).xyz ) /i);
    }

	float B = (D.x+D.y+D.z)*0.15;

    fragColor = vec4( T.x+B*Z, T.y+B*Z, T.z+B*Z, 1.0 );
}
