// Used to have https://soundcloud.com/meryon-1/afterburner but doesnt work anymore
#define gt iTime
#define ic0 iChannel0

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 unit = fragCoord.xy / iResolution.xy;
	vec2 uv = 2.0 * unit - 1.0;
    uv.y *= iResolution.y / iResolution.x;
    uv += vec2(sin(gt), cos(gt)) * 0.5;
    uv *= 2.5;    
    float t = (0.2 * sin(gt * 2.0)) * (0.5 * uv.y + 0.5 * uv.x * (sin(gt) + 1.0) / 1.0);
    float ck = uv.x + uv.y * (sin(gt) + 1.0) / 2.0;
    uv *= mat2(cos(t), -sin(t), sin(t), cos(t));
    uv *= (sin(2.0 + gt + (-uv.x + uv.y / 10.0) / (sin(gt) + 2.0)) + 1.5) *
          (sin(gt / (5.0 + sin(gt / 4.0))) + 1.5);
    uv = mod(uv + 0.5, 1.0) - 0.5;

    float i1 = texture(ic0, vec2(0.1, 0.0)).x;    
    float i2 = texture(ic0, vec2(0.7, 0.0)).x;
    float i2o = max(i1 - 0.5, 0.05) / 3.0;    
    float ix = i2 * 10.0 + uv.x * uv.y;

    float head = smoothstep(0.34, 0.35, length(uv));
    float eyes = smoothstep(0.05 + i2o, 0.06 + i2o, length(vec2(abs(uv.x), uv.y) - vec2(0.15, 0.1)));
    float pupils = smoothstep(0.02, 0.03, length(vec2(abs(uv.x), uv.y) - vec2(0.15 + sin(ix) * 0.03, 0.1 + cos(ix) * 0.03)));
    float mouth = smoothstep(0.24 - i1 * 0.03, 0.25 - i1 * 0.03, length(uv));
    if (uv.y > -0.35 + i1 * 0.4) {
        mouth = 1.0;
    }

    float cm = gt + t + 1.0 * ck;
    vec3 eyeColor = vec3(sin(cm + i1 * 4.5), sin(cm + i1 * 7.5), sin(cm + i1 * 13.5));       
    vec3 color = mix(vec3(0.5, 0.5, 0.5 ), vec3(1.0, 1.0, 1.0), head);
    color = mix(eyeColor, color, eyes);
    color = mix(eyeColor, color, mouth);
    color = mix(vec3(1.0), color, pupils);
	fragColor = vec4(color, 1.0);
}
