vec3 color0 = vec3(1.0,1.0,1.0);
vec3 color1 = vec3(0.0,0.0,1.0);
vec3 color2 = vec3(1.0,0.0, 0.0);
vec3 color3 = vec3(0.0,1.0, 0.0);

vec2 startingPos = vec2(0.2, 0.1);
vec2 endingPos = vec2(0.8, 0.4); 

vec2 chimneyStartPos = vec2(0.3, 0.4);
vec2 chimneyEndPos = vec2(0.4, 0.55);

vec2 windowOneStart = vec2(0.25, 0.25);
vec2 windowOneEnd = vec2(0.35, 0.35);

vec2 windowTwoStart = vec2(0.65, 0.25);
vec2 windowTwoEnd = vec2(0.75, 0.35);



//Layer Statements
bool layer1On = true;
bool layer2On = true;
bool layer3On = true;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float aspectRatio = iResolution.y/iResolution.x;

    uv.y *= aspectRatio;

    vec4 layer0  = vec4(color0, 0.0);
    //House 
    float house = step(uv.x, endingPos.x)*step(uv.y, endingPos.y) * step(startingPos.x, uv.x) * step(startingPos.y, uv.y);
    vec4 layer1 = vec4(color2, house); 

    if(layer1On == false)
    {
        layer1 = vec4(color2, 0.0);
    }
    //Chimney
    float chimney = step(uv.x, chimneyEndPos.x) * step(uv.y, chimneyEndPos.y) * step(chimneyStartPos.x, uv.x)* step(chimneyStartPos.y, uv.y);
   	vec4 layer2 = vec4(color3, chimney);
    if(layer2On == false)
    {
        layer2 = vec4(color2, 0.0);
    }

    //Windows 
    float window1 = step(uv.x, windowOneEnd.x)* step(uv.y, windowOneEnd.y) * step(windowOneStart.x, uv.x) * step(windowOneStart.y, uv.y);
   	float window2 = step(uv.x, windowTwoEnd.x)* step(uv.y, windowTwoEnd.y) * step(windowTwoStart.x, uv.x) * step(windowTwoStart.y, uv.y);
    vec4 layer3 = vec4(color1, window1 + window2);
    if(layer3On == false)
    {
        layer3 = vec4(color2, 0.0);
    }

    vec4 Composite = mix(layer0, layer1, layer1.a);
    Composite = mix(Composite, layer2, layer2.a);
    Composite = mix(Composite, layer3, layer3.a);
    fragColor = Composite;

}
