const float invPI = 0.31830988618379067153776752674503; // 1.0/PI
const float res = 32.0;

float dial = .3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	dial = iMouse.x/iResolution.x; //temp anim

    vec2 uv = fragCoord.xy / iResolution.xy  ;
    uv -= 0.5;
    uv.x *= (iResolution.x /iResolution.y);
    uv = uv*res;
    uv = (uv-fract(uv))/res+.01;
    uv = vec2(((atan(uv.x,uv.y)*invPI)*0.5)+.5,length(uv)); //radial function by bytewave: https://www.shadertoy.com/view/XdXXzf

    float col = smoothstep(dial,dial-.01,uv.x);
    col *= smoothstep(.50,.48,uv.y); 
	fragColor = vec4(col);
}
