//chatting in https://www.shadertoy.com/live

//"Cassini(an) Ovals" are metaball-like elongated 0'S
//they are like ellipses, but 2 distances are multiplied instead of added.
float dCassinoid(vec2 p, vec2 a, vec2 b){
 return length(p-a)*length(p-b);}

float ssf(float a,float d){return smoothstep(-a,a,d);}

float waveTri(float x,float d){
 ;d=fract(d*x);//sawtooth (not c0 continuous)
 ;d=abs(d-.5);//to C0 continuous triangle
 ;return d;   
  //return min(mod(x,d),mod(-x,d))

     ;}

float WavSaw(float x,float w){
 return abs((x)/w);}//waveSaw saw-function

void mainImage(out vec4 r,in vec2 c){
 vec2 res=iResolution.xy;
 vec2 p=c/min(res.x,res.y);
 float dx=res.x/res.y;
 vec2 mr=iMouse.xy/res.xy;//relative mouse position on the screen
 mr.x*=res.x/res.y;
 r.rgb = vec3(p,sin(iTime));

 vec2 a=vec2(.2);
 vec2 b=mr;
 float d=dCassinoid(p,a,b);
 float lq=length(a-b);lq*=lq;
 float dist=lq*(.4+sin(iTime*2.)*.07);
 float eps=0.01;
 float e=1.;
 if(d<dist+eps)e=-.125;
 if(d<dist+eps && d>dist-eps)e*=4.;//optional line
 d=waveTri(d*30.,.6);
 d=1.-ssf(.25,d*e);
 r.rgb=vec3(d);
}
