float radius = 200.0;
float antiAlias = 3.0;
vec4 circleColor = vec4(1.0, 0.0, 0.0, 1.0);
vec4 backColor = vec4(0.0, 0.0, 0.0, 1.0);

vec4 circle(in vec2 pos, in vec2 center)
{
    float d = length(pos - center) - radius;
    float t = smoothstep(0.0, antiAlias, d);
    return vec4(circleColor.rgb, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 midPos = vec2(iResolution.x / 2.0, iResolution.y / 2.0) ;
    vec4 col = circle(fragCoord, midPos);
    col = mix(backColor, col, col.a);
    fragColor = col;

}
