#define N  5   //  cut loop in 2 at N, stop if t=0

#define T(U)  2.* smoothstep(.4,.6, texture(iChannel0, U).r )

void mainImage( out vec4 O, vec2 U )
{
    U /= 32.*iResolution.y;
    float t=1.;

    int i;
    for (i=0; i<N; i++) {
        t *= T(U); U *= 2.;
    }

#if 1  // <<<<<<<<<<<<<<<   
    if (t>0.)
#endif

        for (; i<10; i++) {
            t *= T(U); U *= 2.;
    } 

    O = vec4(exp(-t));    // density to extinction
}
