#define SHIFT 3.0
#define TIME_SCALE .5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;// + vec2(0, iTime * TIME_SCALE);
	float uStep = 1.0 / iResolution.x;



    vec4 color = vec4(0.0,0,0.0,1.0);
    color.x = texture(iChannel0, vec2(uv.x + uStep * SHIFT * 3.0, uv.y)).x;
    color.y = texture(iChannel0, vec2(uv.x + uStep * SHIFT * 2.0, uv.y)).y;
    color.z = texture(iChannel0, vec2(uv.x + uStep * SHIFT * 1.0, uv.y)).z;



	fragColor = color;
    //fragColor = texture(iChannel0, uv);
}
