/*
 * Based heavily on https://www.shadertoy.com/view/XsKGRt
 */

#define DEPTH (1./2.)
bool inters (vec3 v) {
    return (v.z > 1.0-texture(iChannel1, v.xy/2.0).r * DEPTH);
}
vec4 ray(vec2 rs, vec2 uv) {
    vec3 cuv = vec3(uv-vec2(0.5), 1);
    for (int t = 0; t < 150; t++) {
    	vec3 p = vec3(rs, 0)+cuv*(float(t)/128.0);
        if (inters(p)) return vec4(texture(iChannel0, p.xy/2.0).rgb * (1. - p.z) / DEPTH, 1);
    }
    return vec4(0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 tuv = uv+(normalize(vec2(sin(iTime / 10.), cos(iTime / 10.)))*iResolution.xy/100.0);
	fragColor = ray(tuv, uv);
}
