void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 v = texture(iChannel0, uv).rgb;
    vec2 vrt = iMouse.xy/iResolution.xy;
    float f = 0.;
    vec3 v2 = vec3(0);
    for (int i = 0; i < 20; ++i) {
        f++;
   		v2 += texture(iChannel0, mix(uv, vrt,f/20.)).rgb*(f/20.);
    }
	fragColor = vec4(v/2.+v2/10.,1.0);
}
