vec2 CastRay( in vec3 ro, in vec3 rd, mat3 localToWorld )
{
    const float maxd = 5.0;

	float h = 0.5;
    float t = 0.0;
    float steps = 0.0;

    for ( int i = 0; i < 50; ++i )
    {
        if ( h < 0.001 || t > maxd ) 
        {
            break;
        }

	    h = Scene( ro + rd * t, localToWorld );
        t += h;
        steps += 0.03;
    }

    if ( t > maxd )
    {
        t = -1.0;
    }

    return vec2(t, steps);
}

vec3 SceneNormal( in vec3 pos, mat3 localToWorld )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    Scene( pos + eps.xyy, localToWorld ) - Scene( pos - eps.xyy, localToWorld ),
	    Scene( pos + eps.yxy, localToWorld ) - Scene( pos - eps.yxy, localToWorld ),
	    Scene( pos + eps.yyx, localToWorld ) - Scene( pos - eps.yyx, localToWorld ) );
	return normalize( -nor );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
	p.x *= iResolution.x / iResolution.y;

    // have comera zoom in and out to reveal boundaries
	vec3 rayOrigin 	= vec3( 0.0, -0.0, -3.5 + sin( iTime * 2.0) );
    vec3 rayDir 	= normalize( vec3( p.x, p.y, 2.0 ) );

    float theta = -0.5 * MATH_PI;
    mat3 rotX = mat3(
        vec3( cos( theta ), sin( theta ), 0.0 ),
        vec3( -sin( theta ), cos( theta ), 0.0 ),
		vec3( 0.0, 0.0, 1.0 )
        );    

    vec2 mo = iMouse.xy / iResolution.xy;    
    //float phi = 0.25 * MATH_PI + 10.0 * mo.x;    
    //float phi = 0.25 * MATH_PI; 

    // look straight at it. no mouse
    float phi = 0.5 * MATH_PI;    

    mat3 rotY = mat3(
        vec3( 1.0, 0.0, 0.0 ),
        vec3( 0.0, cos( phi ), sin( phi ) ),
        vec3( 0.0, -sin( phi ), cos( phi ) )
        );

    //mat3 localToWorld = rotX * rotY;    
    mat3 localToWorld = rotX * rotY;    


	vec3 color = texture( iChannel0, q ).yyy * 0.3;

	vec2 result = CastRay( rayOrigin, rayDir, localToWorld );

    if ( result.x > 0.0 )
    {
      color = vec3(result.y*2.0, result.y*0.3, result.y*0.2);
    }
    else
    {
      color = vec3(0.0, result.y*abs(sin(iTime*1.8)),0.0);
    }

    fragColor = vec4( color, 1.0 );
}
