void mainImage(out vec4 color, in vec2 pixCoords) {   
	/* == Background == */
	color = vec4(0.9, pixCoords.y / iResolution.y, 0.0, 1.0);

    /* == Sun == */ 
    vec2 center = vec2(3.0 * iResolution.x / 4.0, 3.0 * iResolution.y / 4.0);
    float radius = cos(iTime) * 7.0 + (iResolution.y * 15.0) / 100.0;

    float distSun = sqrt(pow(pixCoords.x - center.x, 2.0) + pow(pixCoords.y - center.y, 2.0));

    if ( distSun < radius ) {
    	color = vec4(1.0, 1.0, 0.0, 1.0);
    }

    /* == Rays == */
    float angleRay = 30.0;

    float deltaX = center.x - pixCoords.x;
    float deltaY = center.y - pixCoords.y;
    float angleRad = ((atan(deltaX, deltaY) * 180.0) / 3.14) + iTime * 2.0;
    if ( (mod(angleRad, angleRay) <= 7.0) && (distSun > radius + 25.0) ) {
        color = vec4(1.0, 1.0, 0.0, 1.0);
    }

    /* == Waves == */
    vec2 dist = pixCoords.xy / iResolution.xy;

    float pi2 = 3.14 * 2.0;
    float waves1 = 3.0; float height1 = 0.18;
    float waves2 = 2.0; float height2 = 0.30;

    float ampli = 0.05;

    float wave1 = ampli * cos((iTime) + dist.x * waves1 * pi2) + height1;
    float wave2 = ampli * cos((iTime * 1.5) + dist.x * waves2 * pi2) + height2;

    if (dist.y < wave2) {
       color = vec4(0.2, 0.2, 0.5, 1.0);
    }

    if (dist.y < wave1) {
        color = vec4(0.2, 0.2, 0.7, 1.0);
    }
}
