void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    uv+=.5;
    float rand = texture(iChannel0, iTime*vec2(.01)).r;
    vec3 col = vec3(0);
    float h = sin(uv.y*30.-iTime-rand*5.)/5.;
    float kl = length((uv)-.5);
    float v = .4+h/10.+rand/5.;
    if (kl<v) {
        float u = abs(kl-v-.002);
        float c = 1.-u;
        c=pow(c,100.);
    	col = vec3(.9*u+atan(c-cos(u)), .75*u, u+sin(c*64.)*u);    
    }
    col*=min(1., iTime);
	fragColor = vec4(col,1.0);
}
