float pi = 3.1414159265;
float a_size = 1.99;
float r_size = 15.00;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    a_size = clamp(a_size,1.0,2.0);
    vec2 uv = (2.0 + sin(iTime)) * 2.0 * fragCoord.xy / iResolution.xy - 1.5;
	uv.x *= iResolution.x / iResolution.y;

    float a = atan(uv.x,uv.y);
    float r = length(uv) / r_size * ( 1.5 + sin ( iTime ) );

    uv = vec2(iTime * .2 + r, a/(pi*a_size));

    vec4 col = texture(iChannel0,uv) * r/.1;
    col.r = mix(col.r, 0.0, 0.85 );
    col.g = mix(col.g, 0.0, 0.85 );
    col.b = mix(col.b, 0.0, 0.85 );

    fragColor = col * 1.1;
}
