void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   float balanco=cos(iTime+uv.x*2.0)*0.02+sin(uv.y*4.0+iTime)*0.05;
   float shadown=1.0-balanco*5.0;
    fragColor = vec4(0,0,0,0)+shadown;
    // Output to screen
    if(uv.x<(1.0/1.5+balanco))
    fragColor = vec4(1,1,0,0)*shadown;
    if(uv.x<1.0/3.0+balanco)
    fragColor = vec4(1,0,0,0)*shadown;

}
