// ^ look at Buf A for the actual implementation

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
}

const int SCALE = 4;

// not really random but close enough
float rand(vec2 n)
{
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 4358.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord -= mod(fragCoord, float(SCALE));

    if (iFrame == 0 || iMouse.z > .5)
    {
        fragColor = vec4(step(.5, rand(fragCoord.xy+iTime)));
    }
    else
    {
        vec2 o = float(SCALE) / iResolution.xy;
        vec2 uv = (fragCoord / iResolution.xy) + (o * .5);

        float accum = 0.;
        float self = 0.;
        for (int i = -1; i <= 1; ++i)
        {
            for (int j = -1; j <= 1; ++j)
            {
				float s = texture(iChannel0, uv + o * vec2(i, j)).r;
                if (i == 0 && j == 0)
                    self = s;
                else
                    accum += s;
            }
        }

        fragColor = vec4(accum == 3. || (accum == 2. && self == 1.));
    }
}
