void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float colorBits = 8.;
    float pixelSize = 16.;
    const int multiSample = 2;
	vec2 uv = (floor(fragCoord.xy/pixelSize)*pixelSize) / iResolution.xy;
    vec4 color = vec4(0,0,0,0);
    for(int x = 0; x < multiSample; x++) {
    	for(int y = 0; y < multiSample; y++) {
    		color += texture( iChannel0, uv+(vec2(x,y)/iResolution.xy));
        }
    }
    color /= float(multiSample*multiSample);
    color = floor(color*colorBits)/colorBits; 
	fragColor = color;
}
