void mainImage( out vec4 fragColor, in vec2 fragCoord )	{
	vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= float(iResolution.x)/iResolution.y;
    float t = iTime*2.;    
	float freq = cos(t*.05)/1.825;
	for(float i=1.;i<75.;i++)  {uv.x += freq/i*cos(i*uv.y+t) +0.494*i;
                                uv.y += freq/i*sin(i*uv.x+t) -0.458*i;}
	float bias = abs(sin(uv.y/0.5));
    vec3 col_a = vec3(1.5, .06, .09);
    vec3 col_b = vec3(.5, 2., 2.4);
    vec3 col   = ((col_a*col_a)*bias+(col_b*col_b)*(1.-bias))/2.;
	fragColor  = vec4(sqrt(col), 1.0);
}
