void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 orig = vec2(0.5, 0.5);
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 relUV = uv - orig;
    float r = 0.35;

    vec4 color;

    float dist = sqrt((relUV.x * relUV.x) + (relUV.y * relUV.y));
    float maxAngle = radians(150.0 * sin(iTime));

    if(dist < r)
    {
        float angle = maxAngle * (1.0 - (dist / r));

        float s = sin(angle);
  		float c = cos(angle);

        // rotate point
  		float xnew = relUV.x * c - relUV.y * s;
  		float ynew = relUV.x * s + relUV.y * c;

        vec2 p = vec2(xnew, ynew);
        p += orig;

        color = texture(iChannel0, p);
    }
    else
    {
        color = texture(iChannel0, uv);
    }

	fragColor = color;
}
