float hash( float n ) { return fract(sin(n)*753.5453123); }
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*157.0 + 113.0*p.z;
    return mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                   mix( hash(n+157.0), hash(n+158.0),f.x),f.y),
               mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                   mix( hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.x;
	float fireVal = (distance(uv.x, 0.5 + (sin((uv.y - iTime*0.5)*10.0)*0.05)*uv.y) > sin(uv.y) * 0.3) ? 0.0 : sin(0.6-uv.y);
    fireVal = max(fireVal, 0.0);
    float landVal = (uv.y < (0.12+sin(uv.x*4.0)*0.08)+(noise(vec3(uv.x*1000.0, 0.0, 0.0))*0.05)*noise(vec3(uv.x * 10.0, 0.0, 0.0))) ? 0.0 : 1.0;
    vec2 uvN = uv * 100.0;
    float starRot = iTime * 0.02;
	vec2 rotVec = vec2(uvN.x*cos(starRot) - uvN.y * sin(starRot), uvN.x*sin(starRot) + uvN.y * cos(starRot));
    float starVal = ((noise(vec3(rotVec.x, rotVec.y, 0.0)) < 0.03) ? 1.0 : 0.0) * landVal * sin(uv.y);
    fragColor = 
        vec4(sin(1.0-uv.y)*0.3*landVal, 0.1*landVal, 0.2*landVal, 1.0) +
        vec4(starVal, starVal, starVal, 1.0) + 
        vec4((1.0-sin(uv.y))*fireVal*((1.0-uv.y)*3.0), sin(uv.y)* fireVal * ((1.0-uv.y)*3.0), fireVal * 0.1, 1.0);
}
