float plot(float d) {
	return smoothstep(3.0 / iResolution.y, 0.0, abs(d));
}

float segment(vec2 p, vec2 a, vec2 b) {
    vec2 ab = b - a, ap = p - a;
    return length(ap - ab * clamp(dot(ab, ap) / dot(ab, ab), 0.0, 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 mo = (iMouse.xy * 2.0 - iResolution.xy) / iResolution.y;
    fragColor = vec4(mix(vec3(1.0), vec3(0.0), plot(segment(uv, vec2(0.0), mo))), 1.0);
}
