#define BRANCHES 5.
#define BIAS_FACTOR .4
#define MORPH_FORCE .15
#define RADIUS .5
#define SMOOTH .02

#define R_SPEED .05
#define M_SPEED 1.3

#define PI 3.1415
#define TPI 6.2831

// https://iquilezles.org/articles/palettes
// cosine based palette, 4 vec4 params
vec4 palette( in float t, in vec4 a, in vec4 b, in vec4 c, in vec4 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

// http://blog.demofox.org/2012/09/24/bias-and-gain-are-your-friend/
float getBias(float x,float bias)
{
  return (x / ((((1.0/bias) - 2.0)*(1.0 - x))+1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y - vec2(.5);

    float d = length(uv);

    float a = ((atan(uv.x,uv.y) + PI) / (TPI)) + iTime * R_SPEED;
    float t = (sin(iTime * M_SPEED) * .5 + .5);
    d += getBias((sin(a * TPI * BRANCHES) * .5 + .5),BIAS_FACTOR) * (MORPH_FORCE * t);

    float f = smoothstep(RADIUS - SMOOTH, RADIUS + SMOOTH, d);


	fragColor = mix(palette(t*.5,vec4(.9,.9,1.5, 1.),vec4(.5,.5,.5, 1.),vec4(1.,1.,.5, 1.),vec4(.8,.9,.3, 1.)),vec4(0.),f);
}
