void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float blur = 10.0;
    vec2 dir = vec2(1,0);

    vec4 sum = vec4(0);

    sum += texture(iChannel0, uv - vec2(0.014,0)) * 0.1;
    sum += texture(iChannel0, uv - vec2(0.013,0)) * 0.1;
    sum += texture(iChannel0, uv - vec2(0.012,0)) * 0.1;
    sum += texture(iChannel0, uv - vec2(0.011,0)) * 0.1;

    sum += texture(iChannel0, uv) * 0.2;

    sum += texture(iChannel0, uv + vec2(0.011,0)) * 0.1;
    sum += texture(iChannel0, uv + vec2(0.012,0)) * 0.1;
    sum += texture(iChannel0, uv + vec2(0.013,0)) * 0.1;
    sum += texture(iChannel0, uv + vec2(0.014,0)) * 0.1;

    vec4 tex = texture(iChannel0, uv);

    fragColor = vec4(sum.rgb, 1.0);
    //fragColor = tex;
}
