#define S 8.

void mainImage( out vec4 O, vec2 I )
{
    vec2 R=iResolution.xy;
    I=I+I-R;
    O-=O;
    for (float i=0.;i<S+S;i++)
    {
        I*=mat2(O.y=cos(O.x=acos(-1.)*i/S),O.z=sin(O.x),-O.z,O.y);
        O.a=max(O.a,texture(iChannel0,(I+R)/(R+R)).r);
    }       
	O =O.aaaa;
}

