void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = vec4(1);

    float offset = 0.5;

    float power = 0.05;
    vec4 lastMouse = texture(iChannel0, vec2(0.1, 0));
    power = lastMouse.x / iResolution.x - offset;
    float lastGlobalTime = texture(iChannel0, vec2(0.2, 0)).x;
    power *= max(1.0 - (iTime - lastGlobalTime), 0.0);

    float x = sin(iTime * 5.0 * power / 0.05) * power;    
    float t = smoothstep(0.0, 1.0, 1.0 - abs(uv.y - 0.5) * 2.0);

    x *= t;
    x += offset;

    if (abs(uv.x - x) < 0.002)
    {
        color = vec4(0);
    }

	fragColor = color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = vec4(0.0,0.0,1.0,1.0);

    if (uv.x < 0.15 && uv.y < 0.05)
    {
        fragColor = iMouse;
    }
    else if (uv.x < 0.25 && uv.y < 0.05)
    {
        if (iMouse.z > 0.5)
        	fragColor = vec4(iTime);
        else
        {
            float clickedTime = texture(iChannel0, vec2(0.2, 0.0)).x;
            fragColor = vec4(min(clickedTime + 0.0001, iTime));
        }
    }
}
