//-----------------------------------------------
// StrangeSoundPattern
// original https://www.shadertoy.com/view/lt3XWs
//-----------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // reflect, scale v1
	uv = 0.5*abs(2.0*(uv-0.5));
	uv.y *=5.5;

    // reflect, scale v2
//	uv.x = 0.5*abs(2.0*(uv.x-0.5));
//	uv.y = 5.5*(uv.y-0.2); 

    // sample fft
    float x = uv.x; 
	float y=abs(texture( iChannel0, vec2(uv.x,0.25) ).x ); // 0.25 = fft
	float z=abs(texture( iChannel0, vec2(uv.x,0.50) ).x ); 
	float w=abs(texture( iChannel0, vec2(uv.x,0.75) ).w ); 

    // determine step
    float a = uv.y*0.05; 
    float b = uv.x*0.35; 
    float dx = a-b;

    // step along, multiplying..
    for (int i=1;i<3;i++) {
        x = x+dx;  // step
        y *= abs( texture( iChannel0, vec2(x,0.25) ).x );
        z *= abs( texture( iChannel0, vec2(x,0.25) ).x );
        w *= abs( texture( iChannel0, vec2(x,0.25) ).x );
    }

    y*=0.45;
    fragColor = vec4( sin(y*3.141*2.0), sin(z*3.141*1.6),sin(w*3.141*1.2),1.0);

}
