// Author: blackpolygon
// Title:  Transformations

#define PI 3.14159265359

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/min(iResolution.x,iResolution.y);
    st.x -= 0.35;

    st -= vec2(0.5);
    st = rotate2d( 0.5 + sin(iTime) ) * st;
    st *= 3. + sin(iTime)*3.4;
    st = fract(st);

    float w = 0.5 + sin(iTime)*0.5;
    float y = step(w,st.x-0.02);
    float z = step(w,st.y-0.02);
    float yy = step(w, .98-st.x);
    float zz = step(w, .98-st.y);

    vec3 color = vec3(y);
    vec3 color2 = vec3(z);
    vec3 color3 = vec3(yy);
    vec3 color4 = vec3(zz);

	vec3 baseColor = vec3(st.x+0.163,st.y+0.076, 1.115);
    vec3 baseColor2 = ((0.5+sin(iTime)*0.3)) *vec3(0.227,0.257,0.540);

    color = color + color2 + color3 + color4;

    if(sin(iTime) < 0.3){
        color = baseColor2 * color;
    }else{
         color = baseColor * color;
    }

	fragColor = vec4(color,1);
}
