#if 1
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;    
    //float ratio = iResolution.x / iResolution.y;
    float offset = 0.;


    float t1 = uv.x + uv.y;
    float band = .5;

    if(t1 > 0. && t1 < 1.*band)
    {
        offset = -1.*sin(iTime);
    }
    else if(t1 >band && t1 < 2.*band)
    {
    	offset = 1. * sin(iTime);
    }
    else if(t1 > 2.*band && t1 < 3.*band)
    {
    	offset = -1.*sin(iTime);
    }
    else if(t1 > 3.*band)
    {
    	offset = 1. * sin(iTime);
    }


	vec4 col0 = texture(iChannel0, uv + vec2(offset, - offset)/*, -10.*/);

    vec4 col1 = vec4(smoothstep(0.,0.015, abs(t1 - 1.*band)));
    vec4 col2 = vec4(smoothstep(0.,0.015, abs(t1 - 2.*band)));
    vec4 col3 = vec4(smoothstep(0.,0.015, abs(t1 - 3.*band)));

    fragColor = mix(col0, vec4(1.), (1.- col1)+(1.-col2)+(1.-col3));

}
