#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position = iResolution.xy / 2.0;

    float radius = (iResolution.y > iResolution.x ? iResolution.x : iResolution.y) * .8;
    float bulge = 1.125;

    vec2 distance = fragCoord.xy - position.xy;

    float distSqr = pow(distance.x,2.0) + pow(distance.y,2.0);

    if (distSqr < pow(radius,2.0))
    {
        float dist = sqrt(distSqr);
        float distortionFactor =  dist * pow(1.0-(dist / radius), 1.0 / bulge);
        fragCoord.xy -= distortionFactor * (distance.xy / dist).xy;
    }

    vec3 col = texture(iChannel0,fragCoord.xy / iResolution.xy).xyz;

    fragColor = vec4( col, 1.0 );
}
