bool isGreen( in vec4 fragColor );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;
    vec4 t = texture(iChannel0, xy);

    if (isGreen(t))
    {
        t = texture(iChannel1, xy);
    }


    fragColor = t;
}

bool isGreen( in vec4 c )
{
    float thresh = 0.25;
    return c.g > thresh && c.r < thresh && c.b < thresh;
}
