#define TAU 6.283185307179

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;

    const float gridNum = 3.;

    vec2 gpos = fract(uv*gridNum*.5);

    // translate to center
    gpos = gpos * 2. - 1.;

    // diagonal distance 
    float diag = (abs(gpos.x) + abs(gpos.y)) * .5;

    float colOffset = 0.05*TAU;
    vec3 colOffsetv = vec3(-colOffset, 0, colOffset);

    const float stripeNum = 3.;
    // diagonal stripes  
    vec3 val = vec3(sin(diag * stripeNum * TAU - iTime * TAU + colOffsetv));

    float b = 100./iResolution.y;
    vec3 col = smoothstep(-b, b, val);

    fragColor = vec4(col,1.0);
}
