void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const bool lines = true;
    int scale = 3;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2(fragCoord.x/(iResolution.x / 3.0), fragCoord.y/(iResolution.y / 2.0));

    float speed = 32.0; //divisor

    // Time varying pixel color    
    float r = 20.0 + 64.0*cos(sin(iTime/(speed * 2.0))+uv.x) + 64.0*sin(cos(iTime/(speed / 2.0))+uv.y);
    float g = 40.0 + 50.0*sin(sin(iTime/(speed))+uv.x) + 50.0*cos(cos(iTime/(speed))+uv.y);
    float b = 60.0 + 64.0*sin(sin(iTime/(speed / 2.0))+uv.x) + 64.0*sin(cos(iTime/(speed * 2.0))+uv.y);


    if(lines) {

    if(int(r) % scale == 0)
    {
        r = 0.0;
    }

    if(int(g) % scale == 0)
    {
        g = 0.0;
    }

    if(int(b) % scale == 0)
    {
        b = 0.0;
    }

    }

    vec3 col = vec3(r/255.0, g/255.0, b/255.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
