void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float centerX = (iResolution.x / 2.0);
   	float centerY = (iResolution.y / 2.0);

    fragCoord.x = (fragCoord.x - centerX);
    fragCoord.y = (fragCoord.y - centerY);

    vec2 center = vec2(0,0);
    float radius = (iResolution.y * 25.0) / 100.0;

    vec4 colorBase = smoothstep(2.,0.,length(fragCoord - center) - radius) * vec4(0.5,0.5,0.,1.0);

	vec3 sun = vec3(cos(iTime * 3.0), 1.0, sin(iTime * 3.0));
     float z = sqrt(pow(radius, 2.0) - pow(fragCoord.x, 2.0) - pow(fragCoord.y, 2.0));
    vec3 pixel = vec3(fragCoord.x, fragCoord.y, z);
    vec3 haut = pixel / radius;
    float lum = dot(haut, sun);
    fragColor = colorBase * max(lum, 0.2);
    // Output to screen
    //fragColor = vec4(0.,0.,0.,1.0);



}
