float DDot(vec2 p1,vec2 p2) {
    return 1.3-(distance(p1,p2)*50.);
}


float Wobble(float i) {
	return sin((i+iTime+i)*2.)/90.+sin((i+iTime)/3.)/85.+sin(iTime/5.)/85.;   
}

float Curv(float i,float ii) {
	return(cos(iTime+i+ii)*(sin((iTime*i)*10.)/(20.*atan(iTime))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    vec3 col1=vec3(0.);

    for (float i=0.00; i<= 1.05;i+=0.1) {

        for (float ii=1.; ii<=3.; ii++) {
   			float col = DDot(uv, vec2(i+Curv(i,sin(iTime)),0.5+Wobble(i)+Curv(i,ii)));
            vec3 col2 = vec3(col * ii, col*(sin(i)+.5),col*(sin(ii)+.5));
    		col1 = max(col2,col1);
        }
    //col1 = max(max(col,m),col1);
    }
    fragColor = vec4(col1,0.);
}
