#define redOffset vec2(-0.01, 0.)
#define greenOffset vec2(0.)
#define blueOffset vec2(0.01, 0.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color;
    color.r = texture(iChannel0, uv + redOffset).r;
    color.g = texture(iChannel0, uv + greenOffset).b;
    color.b = texture(iChannel0, uv + blueOffset).g;
    color.a = texture(iChannel0, uv).a;

    // Output to screen
    fragColor = color;
}
