const float squares = 16.0;
const float amt = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;

    vec2 tc = uv;
    // move texcoords to center
    tc -= 0.5;

    // adjust for perfect circle
    tc.x *= aspect;    

    // pixellate
    tc = floor(tc * squares + 0.5) / squares;

    // create the circle mask
    float mask = length(tc);

    // mod the mask so that we get alternating rings
    mask =  mod(mask - iTime*0.2, 2.0);

    // step the mask to threshold it to black and white
    mask = step(mask, 1.0);

    vec2 ttc = uv;
  	ttc.x *= aspect;
    //ttc = ttc * 2.0 - 1.0;
    //vec2 tile = mod( ttc * squares - vec2(aspect*2.0,0.5) , 1.0) *amt;

	//tile = tile *0.5 + 0.5;

    //uv = uv + tile;
    // sample textures and choose mask
	fragColor =mix(texture(iChannel0, uv ), texture(iChannel1, uv), mask);

}
