void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

highp float rand(vec2 co)
{
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iTime < 2.0){
        float r = rand(fragCoord+iTime);
        float g = rand(fragCoord+iTime+0.41567);
        float b = rand(fragCoord+iTime+0.13498);
        fragColor = vec4(r,g,b,0.0);
    }else{
        vec2 uv = fragCoord.xy / iResolution.xy;
        vec3 d = vec3(1.0 / iResolution.xy, 0.0);
        vec4 t = vec4(0.0);
        t += texture(iChannel0, uv);
        t += texture(iChannel0, uv+d.xz);
        t += texture(iChannel0, uv-d.xz);
        t += texture(iChannel0, uv+d.zy);
        t += texture(iChannel0, uv-d.zy);
        t /= 5.0; t -= 0.001;
        mat4 mat = mat4(
            30.0, 1.0, 1.0, 1.0,
            1.0, 30.0, 1.0, 1.0,
            1.0, 1.0, 30.0, 1.0,
            1.0, 1.0, 1.0, 30.0
        );
        t = mat * t;
        t /= 33.0;
        float r = rand(fragCoord*vec2(5.279, 6.645)+iTime+0.424278);
        float g = rand(fragCoord*vec2(3.512, 9.146)+iTime+0.872213);
        float b = rand(fragCoord*vec2(4.517, 8.614)+iTime+0.531287);
        t += (vec4(r,g,b,0.0)-0.5) * 0.001;
        fragColor = mod(t, 1.0);
    }
}
